update print_layout set template = ''
where id='INVENTORY_BOOK_DETAIL';
--##
update print_layout set template = ''
where id='ACCOUNT_BOOK_GROUP';
--##
with a as (select id, template from print_layout where id = 'INVENTORY_BOOK_DETAIL')
update print_template pt set template=a.template from a where pt.layout=a.id and pt.is_default = true;

with a as (select id, template from print_layout where id = 'ACCOUNT_BOOK_GROUP')
update print_template pt set template=a.template from a where pt.layout=a.id and pt.is_default = true;
--##